namespace SubtitleKitLibTests.Subtitle
{
    using System;

    using SubtitleKitLib.Subtitle;

    using Xunit;

    public class SubtitleCreatorTest
    {
        [Fact]
        public void Subtitle_Created_WithBadUri_ThrowsFormatException()
        {
            var uri = new Uri(@"http://www.contoso.com/path???/file name");
            var subtitleCreator = new SubtitleCreator();

            Assert.Throws<FormatException>(() => subtitleCreator.CreateFromUri(uri));
        }

        [Fact]
        public void Subtitle_Created_WithInvalidStringSub_ThrowsFormatException()
        {
            var invalidSubString = @"   
                {d25d}{1175}\n
                {1d187}{1275}- Tout est en place|- Tu n'étais pas sensé me relever.\n
                {12d87}{1s350}Je sais, mais je pensais me débrouiller.\n
                {1362}{1425}Tu l'aimes bien, non ?|T'aimes le regarder.\n
                {14f37}{1f500}- Ne sois pas ridicule.|- On va le tuer. Tu comprends ça ?\n
                {1512}{1d575}Morpheus pense qu'il est l'Elu.\n
                {15z87}{1600}Et toi ?\n
                {16a12}{1s650}Peu importe ce que je crois.\n
                {1662}{17z00}Tu ne crois pas, hein ?\n";
            var subtitleCreator = new SubtitleCreator();

            Assert.Throws<FormatException>(() => subtitleCreator.CreateFromString(invalidSubString));
        }

        [Fact]
        public void Subtitle_Created_WithInvalidSub_ThrowsFormatException()
        {
            Assert.Throws<FormatException>(
                () => SubtitleContainer.GetSubtitleFromFile(SubtitleContainer.InvalidSubtitleName));
        }

        [Fact]
        public void Subtitle_Created_WithInvalidSub_ThrowsFormatException2()
        {
            Assert.Throws<FormatException>(
                () => SubtitleContainer.GetSubtitleFromFile(SubtitleContainer.InvalidSubtitleName2));
        }

        [Fact]
        public void Subtitle_Created_WithInvalidUriSub_ThrowsFormatException()
        {
            var uri = new Uri("https://pastebin.com/raw/VTQTK2fU");
            var subtitleCreator = new SubtitleCreator();

            Assert.Throws<FormatException>(() => subtitleCreator.CreateFromUri(uri));
        }

        [Fact]
        public void Subtitle_Created_WithInvalidUriSub_ThrowsFormatException2()
        {
            var uri = new Uri("http://temporalis.io/");
            var subtitleCreator = new SubtitleCreator();

            Assert.Throws<FormatException>(() => subtitleCreator.CreateFromUri(uri));
        }

        [Fact]
        public void Subtitle_Created_WithNull_ThrowsArgumentNullException()
        {
            var subtitleCreator = new SubtitleCreator();

            Assert.Throws<ArgumentNullException>(() => subtitleCreator.CreateFromFile(null));
        }

        [Fact]
        public void Subtitle_Created_WithNullString_ThrowsArgumentNullException()
        {
            var subtitleCreator = new SubtitleCreator();

            Assert.Throws<ArgumentNullException>(() => subtitleCreator.CreateFromString(null));
        }

        [Fact]
        public void Subtitle_Created_WithNullUri_ThrowsArgumentNullException()
        {
            var subtitleCreator = new SubtitleCreator();

            Assert.Throws<ArgumentNullException>(() => subtitleCreator.CreateFromUri(null));
        }

        [Fact]
        public void Subtitle_Created_WithValidStringSub_IsNotNull()
        {
            var validSubString =
                "1\r\n00:00:01,000 --> 00:00:04,074\r\nSubtitles downloaded from www.OpenSubtitles.org\r\n\r\n2\r\n00:00:44,353 --> 00:00:46,328  X1:243 X2:478 Y1:374 Y2:410\r\nThere\'s an old joke.\r\n\r\n3\r\n00:00:46,401 --> 00:00:49,249  X1:182 X2:540 Y1:334 Y2:409\r\nTwo elderly women are\r\nat a Catskill mountain resort.\r\n\r\n4\r\n00:00:49,315 --> 00:00:52,447  X1:161 X2:561 Y1:334 Y2:409\r\nOne says, \"The food at this place\r\nis really terrible.\"\r\n\r\n5\r\n00:00:52,515 --> 00:00:55,385  X1:161 X2:561 Y1:334 Y2:409\r\nThe other says,\r\n\"l know, and such small portions.\"\r\n\r\n6\r\n00:00:55,460 --> 00:00:58,308  X1:237 X2:485 Y1:334 Y2:409\r\nThat\'s essentially\r\nhow l feel about life:\r\n\r\n7\r\n00:00:58,372 --> 00:01:01,952  X1:187 X2:534 Y1:334 Y2:409\r\nfull of loneliness, misery,\r\nsuffering and unhappiness...\r\n\r\n8\r\n00:01:02,022 --> 00:01:05,056  X1:257 X2:465 Y1:334 Y2:409\r\nand it\'s all over\r\nmuch too quickly.\r\n\r\n9\r\n00:01:05,127 --> 00:01:07,778  X1:153 X2:568 Y1:374 Y2:410\r\nThe other important joke for me...\r\n\r\n10\r\n00:01:07,847 --> 00:01:10,434  X1:214 X2:508 Y1:334 Y2:409\r\nis one usually attributed\r\nto Groucho Marx...\r\n\r\n11\r\n00:01:10,504 --> 00:01:14,214  X1:123 X2:599 Y1:334 Y2:409\r\nbut it appears originally in Freud\'s\r\nWit and lts Relation to the Unconscious.\r\n\r\n12\r\n00:01:14,281 --> 00:01:16,421  X1:157 X2:564 Y1:374 Y2:410\r\nlt goes like this, l\'m paraphrasing:\r\n\r\n13\r\n00:01:16,490 --> 00:01:20,681  X1:100 X2:621 Y1:334 Y2:409\r\nl never want to belong to any club that\r\nwould have someone like me for a member.\r\n\r\n14\r\n00:01:20,747 --> 00:01:25,418  X1:111 X2:611 Y1:334 Y2:409\r\nThat\'s the key joke of my adult life\r\nin terms of my relationships with women.\r\n\r\n15\r\n00:01:25,484 --> 00:01:28,551  X1:150 X2:571 Y1:334 Y2:409\r\nLately the strangest things\r\nhave been going through my mind...\r\n\r\n16\r\n00:01:28,621 --> 00:01:32,102  X1:166 X2:556 Y1:334 Y2:409\r\nbecause l turned 40, and l guess\r\nl\'m going through a life crisis.\r\n\r\n17\r\n00:01:32,174 --> 00:01:34,793  X1:166 X2:556 Y1:334 Y2:409\r\nl\'m not worried about aging.\r\nl\'m not one of those characters.\r\n\r\n18\r\n00:01:34,862 --> 00:01:38,311  X1:125 X2:596 Y1:334 Y2:409\r\nBut l\'m balding slightly on top. That\'s\r\nabout the worst you can say about me.\r\n\r\n19\r\n00:01:38,384 --> 00:01:41,581  X1:186 X2:536 Y1:334 Y2:409\r\nl think l\'m going to get better\r\nas l get older.\r\n\r\n20\r\n00:01:41,648 --> 00:01:44,496  X1:217 X2:505 Y1:334 Y2:409\r\nl think l\'m going to be\r\nthe balding, virile type...\r\n\r\n21\r\n00:01:44,561 --> 00:01:48,239  X1:133 X2:589 Y1:374 Y2:410\r\nas opposed to the distinguished gray.\r\n\r\n22\r\n00:01:48,306 --> 00:01:50,216  X1:170 X2:552 Y1:374 Y2:410\r\nUnless l\'m neither of those two.\r\n\r\n23\r\n00:01:50,291 --> 00:01:52,680  X1:122 X2:600 Y1:334 Y2:409\r\nUnless l\'m one of those guys with saliva\r\ndribbling out of his mouth...\r\n\r\n24\r\n00:01:52,755 --> 00:01:57,197  X1:107 X2:614 Y1:334 Y2:409\r\nwho wanders into a cafeteria with a\r\nshopping bag, screaming about socialism.\r\n\r\n25\r\n00:01:58,133 --> 00:02:01,647  X1:175 X2:546 Y1:334 Y2:409\r\nAnnie and l broke up, and l still\r\ncan\'t get my mind around that.\r\n\r\n26\r\n00:02:01,718 --> 00:02:05,134  X1:135 X2:586 Y1:334 Y2:409\r\nl keep sifting the pieces\r\nof the relationship through my mind...\r\n\r\n27\r\n00:02:05,207 --> 00:02:09,878  X1:119 X2:603 Y1:334 Y2:409\r\nexamining my life and trying to\r\nfigure out where did the screwup come.\r\n\r\n28\r\n00:02:09,944 --> 00:02:12,759  X1:194 X2:528 Y1:374 Y2:410\r\nA year ago we were in love.\r\n\r\n29\r\n00:02:14,297 --> 00:02:17,811  X1:164 X2:557 Y1:334 Y2:409\r\nlt\'s funny. l\'m not a morose type.\r\nl\'m not a depressive character.\r\n\r\n30\r\n00:02:19,163 --> 00:02:21,465  X1:183 X2:538 Y1:374 Y2:410\r\nl was a reasonably happy kid.\r\n\r\n31\r\n00:02:21,531 --> 00:02:24,434  X1:191 X2:530 Y1:334 Y2:409\r\nl was brought up in Brooklyn\r\nduring World War ll.\r\n\r\n32\r\n00:02:25,693 --> 00:02:27,798  X1:231 X2:490 Y1:374 Y2:410\r\nHe\'s been depressed.\r\n\r\n33\r\n00:02:27,869 --> 00:02:30,204  X1:168 X2:554 Y1:334 Y2:409\r\n- Suddenly he can\'t do anything.\r\n- Why are you depressed, Alvy?\r\n\r\n34\r\n00:02:30,270 --> 00:02:34,133  X1:223 X2:499 Y1:334 Y2:409\r\nTell Dr. Flicker.\r\nlt\'s something he read.\r\n\r\n35\r\n00:02:34,206 --> 00:02:36,116  X1:211 X2:511 Y1:374 Y2:410\r\nSomething he read, huh?\r\n\r\n36\r\n00:02:36,191 --> 00:02:40,284  X1:188 X2:534 Y1:334 Y2:409\r\n- The universe is expanding.\r\n- The universe is expanding?\r\n\r\n37\r\n00:02:40,353 --> 00:02:43,037  X1:197 X2:524 Y1:334 Y2:409\r\nThe universe is everything,\r\nand if it\'s expanding...\r\n\r\n38\r\n00:02:43,105 --> 00:02:46,816  X1:131 X2:590 Y1:334 Y2:409\r\nsomeday it will break apart,\r\nand that will be the end of everything.\r\n\r\n39\r\n00:02:46,882 --> 00:02:51,105  X1:165 X2:556 Y1:334 Y2:409\r\nWhat is that your business?\r\nHe stopped doing his homework!\r\n\r\n40\r\n00:02:51,172 --> 00:02:54,522  X1:124 X2:598 Y1:334 Y2:409\r\n- What\'s the point?\r\n- What\'s the universe got to do with it?\r\n\r\n41\r\n00:02:54,596 --> 00:02:57,826  X1:203 X2:519 Y1:334 Y2:409\r\nYou\'re here in Brooklyn.\r\nBrooklyn is not expanding!\r\n\r\n42\r\n00:02:57,893 --> 00:03:01,724  X1:231 X2:491 Y1:334 Y2:409\r\nlt won\'t be expanding\r\nfor billions of years!\r\n\r\n43\r\n00:03:01,798 --> 00:03:06,534  X1:137 X2:585 Y1:334 Y2:409\r\nWe\'ve got to try and enjoy ourselves\r\nwhile we\'re here! Huh?\r\n\r\n44\r\n00:03:12,137 --> 00:03:14,342  X1:179 X2:543 Y1:334 Y2:409\r\nMy analyst says l exaggerate\r\nmy childhood memories...\r\n\r\n45\r\n00:03:14,410 --> 00:03:16,777  X1:168 X2:554 Y1:334 Y2:409\r\nbut l swear l was brought up\r\nunderneath the roller coaster...\r\n\r\n46\r\n00:03:16,843 --> 00:03:18,948  X1:198 X2:524 Y1:334 Y2:409\r\nin the Coney lsland section\r\nof Brooklyn.\r\n\r\n47\r\n00:03:19,019 --> 00:03:23,144  X1:109 X2:612 Y1:334 Y2:409\r\nMaybe that accounts for my personality,\r\nwhich is a little nervous.\r\n\r\n48\r\n00:03:23,212 --> 00:03:27,753  X1:132 X2:589 Y1:334 Y2:409\r\nl have a hyperactive imagination.\r\nMy mind tends to jump around a little.\r\n\r\n49\r\n00:03:27,821 --> 00:03:31,138  X1:180 X2:542 Y1:334 Y2:409\r\nl have some trouble\r\nbetween fantasy and reality.\r\n\r\n50\r\n00:03:32,014 --> 00:03:34,316  X1:188 X2:534 Y1:334 Y2:409\r\nMy father ran\r\nthe bumper-car concession.\r\n\r\n51\r\n00:03:34,383 --> 00:03:36,970  X1:197 X2:525 Y1:374 Y2:410\r\nThere he is, and there l am.\r\n\r\n52\r\n00:03:37,040 --> 00:03:40,357  X1:168 X2:554 Y1:334 Y2:409\r\nl used to get my aggression out\r\nthrough those cars.\r\n\r\n53\r\n00:03:42,577 --> 00:03:45,033  X1:227 X2:494 Y1:334 Y2:409\r\nl remember the staff\r\nat our public school.\r\n\r\n54\r\n00:03:45,105 --> 00:03:48,303  X1:186 X2:536 Y1:334 Y2:409\r\nWe had a saying,\r\n\"Those who can\'t do, teach...\r\n\r\n55\r\n00:03:48,371 --> 00:03:51,503  X1:194 X2:527 Y1:334 Y2:409\r\nand those who can\'t teach,\r\nteach gym.\"\r\n\r\n56\r\n00:03:51,571 --> 00:03:55,795  X1:143 X2:579 Y1:334 Y2:409\r\nAnd those who couldn\'t do anything,\r\nl think, were assigned to our school.\r\n\r\n57\r\n00:03:58,453 --> 00:04:01,007  X1:184 X2:537 Y1:334 Y2:409\r\nl must say, l always thought\r\nmy schoolmates were idiots.\r\n\r\n58\r\n00:04:01,078 --> 00:04:03,893  X1:226 X2:496 Y1:334 Y2:409\r\nMelvyn Greenglass\r\nand his fat little face.\r\n\r\n59\r\n00:04:03,959 --> 00:04:06,993  X1:206 X2:515 Y1:334 Y2:409\r\nAnd Henrietta Farrell,\r\nMiss Perfect all the time.\r\n\r\n60\r\n00:04:07,064 --> 00:04:10,512  X1:145 X2:577 Y1:334 Y2:409\r\nAnd lvan Ackerman,\r\nalways the wrong answer. Always!\r\n\r\n61\r\n00:04:10,584 --> 00:04:12,440  X1:218 X2:503 Y1:374 Y2:410\r\nSeven and three is nine.\r\n\r\n62\r\n00:04:13,721 --> 00:04:15,609  X1:230 X2:492 Y1:334 Y2:409\r\nEven then l knew\r\nthey were just jerks.\r\n\r\n63\r\n00:04:15,674 --> 00:04:19,024  X1:112 X2:610 Y1:374 Y2:410\r\nln 1942 l had already discovered women.\r\n\r\n64\r\n00:04:21,468 --> 00:04:24,338  X1:279 X2:443 Y1:374 Y2:410\r\nHe kissed me!\r\n\r\n65\r\n00:04:24,413 --> 00:04:26,780  X1:152 X2:569 Y1:334 Y2:409\r\nThat\'s the second time this month!\r\nStep up here.\r\n\r\n66\r\n00:04:26,845 --> 00:04:28,755  X1:267 X2:455 Y1:334 Y2:409\r\n- What did l do?\r\n- Step up here.\r\n\r\n67\r\n00:04:28,829 --> 00:04:31,318  X1:136 X2:585 Y1:334 Y2:409\r\n- What did l do?\r\n- You should be ashamed of yourself.\r\n\r\n68\r\n00:04:31,391 --> 00:04:34,206  X1:193 X2:528 Y1:334 Y2:409\r\nWhy? l was just expressing\r\na healthy sexual curiosity.\r\n\r\n69\r\n00:04:34,272 --> 00:04:37,589  X1:177 X2:545 Y1:334 Y2:409\r\nSix-year-old boys\r\ndon\'t have girls on their minds.\r\n\r\n70\r\n00:04:37,664 --> 00:04:38,776  X1:333 X2:388 Y1:374 Y2:410\r\nl did.\r\n\r\n71\r\n00:04:38,848 --> 00:04:42,591  X1:167 X2:555 Y1:334 Y2:409\r\nFor God sakes, Alvy, even Freud\r\nspeaks of a latency period!\r\n\r\n72\r\n00:04:42,658 --> 00:04:45,692  X1:190 X2:531 Y1:334 Y2:409\r\nl never had a latency period.\r\nl can\'t help it.\r\n\r\n73\r\n00:04:45,762 --> 00:04:47,999  X1:188 X2:534 Y1:334 Y2:409\r\nWhy couldn\'t you\r\nhave been more like Donald?\r\n\r\n74\r\n00:04:48,067 --> 00:04:50,337  X1:218 X2:503 Y1:374 Y2:410\r\nThere was a model boy.\r\n\r\n75\r\n00:04:50,403 --> 00:04:52,673  X1:184 X2:538 Y1:334 Y2:409\r\nTell the folks\r\nwhere you are today, Donald.\r\n\r\n76\r\n00:04:52,740 --> 00:04:56,123  X1:159 X2:562 Y1:374 Y2:410\r\nl run a profitable dress company.\r\n\r\n77\r\n00:04:56,197 --> 00:05:01,77  X1:193 X2:529 Y1:334 Y2:409\r\nSometimes l wonder where\r\nmy classmates are today.\r\n\r\n78\r\n00:05:01,84 --> 00:05:03,557  X1:160 X2:561 Y1:334 Y2:409\r\nl\'m president\r\nof the Pinkus Plumbing Company.\r\n\r\n79\r\n00:05:04,455 --> 00:05:06,278  X1:285 X2:436 Y1:374 Y2:410\r\nl sell talliths.\r\n\r\n80\r\n00:05:06,344 --> 00:05:10,982  X1:188 X2:533 Y1:334 Y2:409\r\nl used to be a heroin addict,\r\nnow l\'m a methadone addict.\r\n\r\n81\r\n00:05:12,009 --> 00:05:14,214  X1:266 X2:455 Y1:374 Y2:410\r\nl\'m into leather.\r\n\r\n82\r\n00:05:15,338 --> 00:05:18,688  X1:112 X2:609 Y1:334 Y2:409\r\nl lost track of most of my old\r\nschoolmates, but l wound up a comedian.\r\n\r\n83\r\n00:05:18,763 --> 00:05:21,666  X1:162 X2:560 Y1:374 Y2:410\r\nThey did not take me in the army.\r\n\r\n84\r\n00:05:21,740 --> 00:05:24,293  X1:300 X2:422 Y1:374 Y2:410\r\nl was 4-P.\r\n\r\n85\r\n00:05:24,364 --> 00:05:27,813  X1:241 X2:481 Y1:334 Y2:409\r\nln the event of war,\r\nl\'m a hostage.\r\n\r\n86\r\n00:05:29,326 --> 00:05:32,622  X1:171 X2:550 Y1:334 Y2:409\r\nYou always only saw the worst\r\nin people.\r\n\r\n87\r\n00:05:32,687 --> 00:05:35,623  X1:204 X2:518 Y1:334 Y2:409\r\nYou never could get along\r\nwith anyone in school.\r\n\r\n88\r\n00:05:35,695 --> 00:05:37,998  X1:184 X2:537 Y1:334 Y2:409\r\nYou were always out of step\r\nwith the world.\r\n\r\n89\r\n00:05:38,064 --> 00:05:40,880  X1:183 X2:538 Y1:334 Y2:409\r\nEven when you got famous,\r\nyou still distrusted the world.\r\n\r\n90\r\n00:05:40,945 --> 00:05:44,459  X1:137 X2:584 Y1:334 Y2:409\r\nl distinctly heard it.\r\nHe muttered under his breath, \"Jew.\"\r\n\r\n91\r\n00:05:44,530 --> 00:05:46,385  X1:271 X2:451 Y1:334 Y2:409\r\n- You\'re crazy.\r\n- No, l\'m not.\r\n\r\n92\r\n00:05:46,450 --> 00:05:49,484  X1:130 X2:591 Y1:334 Y2:409\r\nWe were walking off the tennis court.\r\nHe was there and me and his wife.\r\n\r\n93\r\n00:05:49,555 --> 00:05:51,498  X1:183 X2:539 Y1:334 Y2:409\r\nHe looked her,\r\nthen they both looked at me...\r\n\r\n94\r\n00:05:51,572 --> 00:05:53,645  X1:236 X2:486 Y1:334 Y2:409\r\nand under his breath\r\nhe said, \"Jew.\"\r\n\r\n95\r\n00:05:53,717 --> 00:05:55,605  X1:216 X2:506 Y1:374 Y2:410\r\nYou\'re a total paranoid.\r\n\r\n96\r\n00:05:55,669 --> 00:05:58,452  X1:170 X2:552 Y1:334 Y2:409\r\nHow am l a paranoid?\r\nl pick up on those kind of things.\r\n\r\n97\r\n00:05:58,518 --> 00:06:02,960  X1:124 X2:597 Y1:334 Y2:409\r\nl was having lunch with guys from NBC,\r\nso l said, \"Did you eat yet?\"\r\n\r\n98\r\n00:06:03,031 --> 00:06:07,472  X1:182 X2:540 Y1:334 Y2:409\r\nTom Christie said, \"No. Jew?\"\r\nNot \"Did you?\"\r\n\r\n99\r\n00:06:07,544 --> 00:06:10,676  X1:156 X2:566 Y1:334 Y2:409\r\nNot \"Did you eat?\" but \"Jew eat?\"\r\nYou get it?\r\n\r\n100\r\n00:06:10,745 --> 00:06:13,266  X1:229 X2:492 Y1:334 Y2:409\r\n- Max--\r\n- Stop calling me Max.\r\n\r\n101\r\n00:06:13,337 --> 00:06:16,917  X1:118 X2:604 Y1:334 Y2:409\r\nWhy? lt\'s a good name for you.\r\nMax, you see conspiracies in everything.\r\n\r\n102\r\n00:06:16,987 --> 00:06:19,409  X1:157 X2:564 Y1:334 Y2:409\r\nNo, l don\'t. l was in a record store.\r\nListen to this.\r\n\r\n103\r\n00:06:19,484 --> 00:06:24,853  X1:122 X2:599 Y1:334 Y2:409\r\nThere\'s this tall, blond crew-cutted guy\r\nand he\'s looking at me in a funny way.\r\n\r\n104\r\n00:06:24,925 --> 00:06:27,708  X1:162 X2:560 Y1:334 Y2:409\r\nHe\'s saying, \"Yes, we have a sale\r\nthis week on Wagner.\"\r\n\r\n105\r\n00:06:27,774 --> 00:06:30,906  X1:159 X2:563 Y1:334 Y2:409\r\nWagner, Max.\r\nl know what he\'s trying to tell me.\r\n\r\n106\r\n00:06:30,974 --> 00:06:33,364  X1:194 X2:528 Y1:374 Y2:410\r\nVery significantly, Wagner.\r\n\r\n107\r\n00:06:33,440 --> 00:06:36,856  X1:195 X2:527 Y1:374 Y2:410\r\nRight, Max. California, Max.\r\n\r\n108\r\n00:06:36,929 --> 00:06:39,864  X1:151 X2:571 Y1:334 Y2:409\r\n- Get the hell out of this crazy city.\r\n- Forget it.\r\n\r\n109\r\n00:06:39,937 --> 00:06:42,807  X1:161 X2:560 Y1:334 Y2:409\r\nWe move to sunny L.A.\r\nAll of show business is out there.\r\n\r\n110\r\n00:06:42,882 --> 00:06:45,600  X1:169 X2:553 Y1:334 Y2:409\r\nYou keep bringing it up,\r\nbut l don\'t want to live in a city...\r\n\r\n111\r\n00:06:45,667 --> 00:06:49,629  X1:115 X2:607 Y1:334 Y2:409\r\nwhere the only cultural advantage is you\r\ncan make a right turn on a red light.\r\n\r\n112\r\n00:06:49,700 --> 00:06:52,570  X1:145 X2:577 Y1:334 Y2:409\r\nAll right, forget it. Aren\'t you\r\ngoing to be late for meeting Annie?\r\n\r\n113\r\n00:06:52,645 --> 00:06:55,329  X1:117 X2:604 Y1:334 Y2:409\r\nl\'m meeting her in front of the Beekman.\r\nl have a few minutes left, right?\r\n\r\n114\r\n00:07:11,657 --> 00:07:13,600  X1:225 X2:496 Y1:374 Y2:410\r\nAre you on television?\r\n\r\n115\r\n00:07:18,027 --> 00:07:19,620  X1:244 X2:477 Y1:374 Y2:410\r\nYeah, occasionally.\r\n\r\n116\r\n00:07:19,691 --> 00:07:21,317  X1:242 X2:479 Y1:374 Y2:410\r\nWhat\'s your name?\r\n\r\n117\r\n00:07:21,388 --> 00:07:23,244  X1:232 X2:489 Y1:334 Y2:409\r\nYou wouldn\'t know it.\r\nlt doesn\'t matter.\r\n\r\n118\r\n00:07:23,309 --> 00:07:27,172  X1:166 X2:556 Y1:374 Y2:410\r\nYou\'re on Johnny Carson, right?\r\n\r\n119\r\n00:07:28,174 --> 00:07:29,866  X1:269 X2:453 Y1:374 Y2:410\r\nOnce in a while.\r\n\r\n120\r\n00:07:29,935 --> 00:07:32,357  X1:242 X2:479 Y1:374 Y2:410\r\nWhat\'s your name?\r\n\r\n121\r\n00:07:32,431 --> 00:07:35,661  X1:232 X2:489 Y1:334 Y2:409\r\n- l\'m Robert Redford.\r\n- Come on!\r\n\r\n122\r\n00:07:36,688 --> 00:07:39,755  X1:156 X2:566 Y1:334 Y2:409\r\nAlvy Singer.\r\nThanks very much for everything.\r\n\r\n123\r\n00:07:42,737 --> 00:07:44,680  X1:247 X2:474 Y1:374 Y2:410\r\nThis is Alvy Singer!\r\n\r\n124\r\n00:07:46,803 --> 00:07:49,771  X1:176 X2:546 Y1:334 Y2:409\r\nThis guy\'s on television.\r\nAlvy Singer, right? Am l right?\r\n\r\n125\r\n00:07:49,844 --> 00:07:52,081  X1:211 X2:511 Y1:334 Y2:409\r\n- Give me a break.\r\n- This guy\'s on television.\r\n\r\n126\r\n00:07:52,148 --> 00:07:54,004  X1:194 X2:527 Y1:374 Y2:410\r\nl need the large polo mallet.\r\n\r\n127\r\n00:07:54,069 --> 00:07:57,266  X1:130 X2:592 Y1:334 Y2:409\r\n-Who\'s on television?\r\n-This guy, on the Johnny Carson Show.\r\n\r\n128\r\n00:07:57,334 --> 00:08:00,783  X1:136 X2:585 Y1:334 Y2:409\r\n- ls this a meeting of the teamsters?\r\n- What program?\r\n\r\n129\r\n00:08:00,855 --> 00:08:02,743  X1:169 X2:553 Y1:334 Y2:409\r\n- Can l have your autograph?\r\n- You don\'t want my autograph.\r\n\r\n130\r\n00:08:02,807 --> 00:08:05,623  X1:208 X2:514 Y1:374 Y2:410\r\nl do! lt\'s for my girlfriend.\r\n\r\n131\r\n00:08:05,688 --> 00:08:07,630  X1:237 X2:485 Y1:374 Y2:410\r\nMake it out to Ralph.\r\n\r\n132\r\n00:08:07,704 --> 00:08:11,153  X1:158 X2:564 Y1:334 Y2:409\r\n- Your girlfriend\'s name is Ralph?\r\n- lt\'s for my brother.\r\n\r\n133\r\n00:08:14,875 --> 00:08:16,883  X1:207 X2:514 Y1:334 Y2:409\r\nYou\'re really Alvy Singer,\r\nthe TV star?\r\n\r\n134\r\n00:08:17,948 --> 00:08:21,746  X1:218 X2:504 Y1:334 Y2:409\r\n- Alvy Singer over here!\r\n- lt\'s all right, fellas.\r\n\r\n135\r\n00:08:21,820 --> 00:08:24,407  X1:140 X2:582 Y1:334 Y2:409\r\nJesus, what did you do?\r\nCome by way of the Panama Canal?\r\n\r\n136\r\n00:08:24,478 --> 00:08:27,577  X1:152 X2:569 Y1:334 Y2:409\r\n-l\'m in a bad mood.\r\n-l\'m here with the Godfather cast.\r\n\r\n137\r\n00:08:27,646 --> 00:08:31,608  X1:108 X2:613 Y1:334 Y2:409\r\n-Learn to deal with it.\r\n-l\'m dealing with two guys named Cheech.\r\n\r\n138\r\n00:08:31,679 --> 00:08:34,167  X1:202 X2:520 Y1:374 Y2:410\r\nPlease, l have a headache.\r\n\r\n139\r\n00:08:34,239 --> 00:08:37,175  X1:162 X2:559 Y1:334 Y2:409\r\nYou are in a bad mood.\r\nYou must be getting your period.\r\n\r\n140\r\n00:08:37,249 --> 00:08:39,159  X1:165 X2:556 Y1:374 Y2:410\r\nl\'m not getting my period! Jesus!\r\n\r\n141\r\n00:08:39,234 --> 00:08:42,169  X1:121 X2:600 Y1:334 Y2:409\r\nEvery time anything out of the ordinary\r\nhappens, you think l have my period.\r\n\r\n142\r\n00:08:42,242 --> 00:08:44,861  X1:190 X2:531 Y1:334 Y2:409\r\nA little louder. l think one\r\nof them may have missed it.\r\n\r\n143\r\n00:08:44,931 --> 00:08:47,998  X1:200 X2:522 Y1:334 Y2:409\r\n- Has the picture started?\r\n- Two minutes ago.\r\n\r\n144\r\n00:08:48,068 --> 00:08:50,173  X1:247 X2:474 Y1:334 Y2:409\r\nThat\'s it. Forget it.\r\nl can\'t go in.\r\n\r\n145\r\n00:08:50,244 --> 00:08:53,027  X1:212 X2:510 Y1:334 Y2:409\r\n- Two minutes.\r\n- We\'ve blown it already.\r\n\r\n146\r\n00:08:53,093 --> 00:08:55,395  X1:209 X2:513 Y1:374 Y2:410\r\nl can\'t go in, in the middle.\r\n\r\n147\r\n00:08:55,462 --> 00:08:58,278  X1:164 X2:558 Y1:334 Y2:409\r\nln the middle? We\'ve only missed\r\nthe titles. They\'re in Swedish.\r\n\r\n148\r\n00:08:58,343 --> 00:09:00,198  X1:120 X2:601 Y1:374 Y2:410\r\nYou want to get coffee for two hours?\r\n\r\n149\r\n00:09:00,263 --> 00:09:03,395  X1:187 X2:535 Y1:334 Y2:409\r\n- Two hours? No. l\'m going in.\r\n- Go ahead. Good-bye.\r\n\r\n150\r\n00:09:03,464 --> 00:09:06,082  X1:242 X2:480 Y1:334 Y2:409\r\nWhile we\'re talking,\r\nwe could be inside.\r\n\r\n151\r\n00:09:06,152 --> 00:09:09,896  X1:111 X2:611 Y1:334 Y2:409\r\nCan we not argue in front of everybody?\r\nl get embarrassed.\r\n\r\n152\r\n00:09:09,962 --> 00:09:14,436  X1:130 X2:592 Y1:334 Y2:409\r\n- All right. So what do you want to do?\r\n- l don\'t know. Go to another movie?\r\n\r\n153\r\n00:09:14,507 --> 00:09:16,679  X1:148 X2:573 Y1:334 Y2:409\r\n-Let\'s see The Sorrow and the Pity.\r\n-We\'ve seen it.\r\n\r\n154\r\n00:09:16,747 --> 00:09:19,715  X1:151 X2:571 Y1:334 Y2:409\r\nl\'m not in the mood to see\r\na four hour documentary on Nazis.\r\n\r\n155\r\n00:09:19,788 --> 00:09:21,731  X1:304 X2:418 Y1:374 Y2:410\r\nl\'m sorry.\r\n\r\n156\r\n00:09:21,804 --> 00:09:26,540  X1:144 X2:578 Y1:334 Y2:409\r\nl\'ve got to see a picture exactly\r\nfrom start to finish, \'cause l\'m anal.\r\n\r\n157\r\n00:09:26,606 --> 00:09:28,494  X1:238 X2:483 Y1:334 Y2:409\r\nThat\'s a polite word\r\nfor what you are.\r\n\r\n158\r\n00:09:30,640 --> 00:09:34,699  X1:142 X2:579 Y1:334 Y2:409\r\nWe saw the Fellini film last Tuesday.\r\nlt is not one of his best.\r\n\r\n159\r\n00:09:34,768 --> 00:09:36,744  X1:184 X2:537 Y1:374 Y2:410\r\nlt lacks a cohesive structure.\r\n\r\n160\r\n00:09:36,816 --> 00:09:40,527  X1:128 X2:594 Y1:334 Y2:409\r\nYou get the feeling he\'s not absolutely\r\nsure what it is he wants to say.\r\n\r\n161\r\n00:09:40,594 --> 00:09:45,330  X1:152 X2:569 Y1:334 Y2:409\r\nl\'ve always felt he was essentially\r\na technical filmmaker.\r\n\r\n162\r\n00:09:45,396 --> 00:09:48,364  X1:138 X2:583 Y1:374 Y2:410\r\nGranted La Strada was a great film.\r\n\r\n163\r\n00:09:48,435 --> 00:09:51,436  X1:141 X2:581 Y1:334 Y2:409\r\nGreat in its use of negative imagery\r\nmore than anything else.\r\n\r\n164\r\n00:09:51,509 --> 00:09:54,641  X1:193 X2:528 Y1:334 Y2:409\r\n- l\'m going to have a stroke.\r\n- Stop listening to him.\r\n\r\n165\r\n00:09:55,638 --> 00:09:57,908  X1:135 X2:586 Y1:374 Y2:410\r\nHe\'s screaming his opinions in my ear.\r\n\r\n166\r\n00:09:57,975 --> 00:10:01,271  X1:195 X2:527 Y1:334 Y2:409\r\nAll that Juliet of the Spirits\r\nor Satyricon.\r\n\r\n167\r\n00:10:01,335 --> 00:10:04,402  X1:184 X2:537 Y1:374 Y2:410\r\nl found it incredibly indulgent.\r\n\r\n168\r\n00:10:04,472 --> 00:10:07,539  X1:154 X2:568 Y1:334 Y2:409\r\nHe really is one of\r\nthe most indulgent of filmmakers.\r\n\r\n169\r\n00:10:07,609 --> 00:10:09,814  X1:187 X2:535 Y1:374 Y2:410\r\nKey word here is \"indulgent.\"\r\n\r\n170\r\n00:10:10,842 --> 00:10:14,902  X1:156 X2:566 Y1:334 Y2:409\r\n- What are you depressed about?\r\n- l missed my therapy. l overslept.\r\n\r\n171\r\n00:10:14,971 --> 00:10:16,979  X1:160 X2:562 Y1:374 Y2:410\r\nHow can you possibly oversleep?\r\n\r\n172\r\n00:10:17,051 --> 00:10:18,677  X1:262 X2:460 Y1:374 Y2:410\r\nThe alarm clock.\r\n\r\n173\r\n00:10:18,748 --> 00:10:20,691  X1:161 X2:560 Y1:334 Y2:409\r\nYou know what a hostile gesture\r\nthat is to me?\r\n\r\n174\r\n00:10:20,765 --> 00:10:23,635  X1:223 X2:499 Y1:334 Y2:409\r\nl know. Because of our\r\nsexual problem, right?\r\n\r\n175\r\n00:10:23,709 --> 00:10:27,507  X1:184 X2:538 Y1:334 Y2:409\r\nEverybody in line has to know\r\nour rate of intercourse?\r\n\r\n176\r\n00:10:27,583 --> 00:10:29,339  X1:216 X2:505 Y1:374 Y2:410\r\nlt\'s like Samuel Beckett.\r\n\r\n177\r\n00:10:29,407 --> 00:10:33,085  X1:155 X2:566 Y1:334 Y2:409\r\nl admire the technique,\r\nbut it doesn\'t hit me on a gut level.\r\n\r\n178\r\n00:10:33,152 --> 00:10:35,804  X1:172 X2:549 Y1:334 Y2:409\r\n- l\'d like to hit him on a gut level.\r\n- Stop it.\r\n\r\n179\r\n00:10:35,873 --> 00:10:39,703  X1:215 X2:506 Y1:334 Y2:409\r\nHe\'s spitting on my neck\r\nwhen he talks.\r\n\r\n180\r\n00:10:39,778 --> 00:10:42,812  X1:195 X2:526 Y1:334 Y2:409\r\nYou know something else?\r\nYou\'re so egocentric that...\r\n\r\n181\r\n00:10:42,883 --> 00:10:46,266  X1:133 X2:588 Y1:334 Y2:409\r\nif l miss my therapy you can only think\r\nof it in terms of how it affects you!\r\n\r\n182\r\n00:10:48,421 --> 00:10:50,560  X1:142 X2:580 Y1:374 Y2:410\r\nThey\'re probably on their first date.\r\n\r\n183\r\n00:10:50,629 --> 00:10:54,143  X1:158 X2:564 Y1:334 Y2:409\r\nProbably met by answering an ad\r\nin the New York Review of Books.\r\n\r\n184\r\n00:10:54,214 --> 00:10:56,069  X1:211 X2:511 Y1:334 Y2:409\r\n\"Thirtyish academic\r\nwishes to meet woman...\r\n\r\n185\r\n00:10:56,134 --> 00:11:01,91  X1:193 X2:528 Y1:334 Y2:409\r\nwho\'s interested in Mozart,\r\nJames Joyce and sodomy.\"\r\n\r\n186\r\n00:11:01,97 --> 00:11:01,831  X1:226 X2:496 Y1:334 Y2:409\r\nWhat do you mean\r\n\"our sexual problem\"?\r\n\r\n187\r\n00:11:01,896 --> 00:11:04,318  X1:192 X2:530 Y1:334 Y2:409\r\nl\'m comparatively normal\r\nfor a guy raised in Brooklyn.\r\n\r\n188\r\n00:11:04,392 --> 00:11:08,288  X1:186 X2:535 Y1:334 Y2:409\r\nl\'m sorry. My sexual problem.\r\nOkay? My sexual problem!\r\n\r\n189\r\n00:11:08,362 --> 00:11:12,356  X1:111 X2:611 Y1:334 Y2:409\r\nl never read that. That was Henry James\'\r\nsequel to Turn of the Screw?\r\n\r\n190\r\n00:11:12,427 --> 00:11:14,849  X1:178 X2:544 Y1:374 Y2:410\r\nlt\'s the influence of television.\r\n\r\n191\r\n00:11:14,923 --> 00:11:20,456  X1:149 X2:573 Y1:334 Y2:409\r\nMarshall McLuhan deals with it\r\nin terms of it being a high intensity.\r\n\r\n192\r\n00:11:20,524 --> 00:11:22,532  X1:238 X2:483 Y1:334 Y2:409\r\nDo you understand?\r\nA hot medium.\r\n\r\n193\r\n00:11:22,605 --> 00:11:26,665  X1:139 X2:583 Y1:334 Y2:409\r\nWhat l wouldn\'t give for a large sock\r\nwith horse manure in it.\r\n\r\n194\r\n00:11:26,734 --> 00:11:30,379  X1:148 X2:573 Y1:334 Y2:409\r\nWhat do you do when you get stuck\r\non a movie line with a guy like this?\r\n\r\n195\r\n00:11:30,448 --> 00:11:33,416  X1:157 X2:564 Y1:334 Y2:409\r\nWait. Why can\'t l give my opinion?\r\nlt\'s a free country!\r\n\r\n196\r\n00:11:33,488 --> 00:11:35,878  X1:178 X2:543 Y1:334 Y2:409\r\nHe can.\r\nDo you have to give it so loud?\r\n\r\n197\r\n00:11:35,953 --> 00:11:38,288  X1:213 X2:509 Y1:334 Y2:409\r\nAren\'t you ashamed\r\nto pontificate like that?\r\n\r\n198\r\n00:11:38,354 --> 00:11:42,479  X1:155 X2:566 Y1:334 Y2:409\r\nThe funny part is, you don\'t know\r\nanything about Marshall McLuhan.\r\n\r\n199\r\n00:11:42,547 --> 00:11:46,923  X1:134 X2:587 Y1:334 Y2:409\r\nl happen to teach a class at Columbia\r\ncalled \"TV, Media and Culture.\"\r\n\r\n200\r\n00:11:46,996 --> 00:11:50,642  X1:150 X2:571 Y1:334 Y2:409\r\nl think my insights into Mr. McLuhan\r\nhave a great deal of validity.\r\n\r\n201\r\n00:11:50,709 --> 00:11:55,282  X1:121 X2:600 Y1:334 Y2:409\r\nDo you? That\'s funny, because l happen\r\nto have Mr. McLuhan right here.\r\n\r\n202\r\n00:11:55,350 --> 00:11:58,766  X1:209 X2:512 Y1:334 Y2:409\r\nJust let me--\r\nCome over here. Tell him.\r\n\r\n203\r\n00:11:58,839 --> 00:12:00,749  X1:180 X2:542 Y1:374 Y2:410\r\nl heard what you were saying.\r\n\r\n204\r\n00:12:00,824 --> 00:12:05,135  X1:138 X2:583 Y1:334 Y2:409\r\nYou know nothing of my work.\r\nYou mean my whole fallacy is wrong.\r\n\r\n205\r\n00:12:05,209 --> 00:12:08,341  X1:145 X2:576 Y1:334 Y2:409\r\nHow you ever got to teach a course\r\nin anything is totally amazing!\r\n\r\n206\r\n00:12:08,410 --> 00:12:10,931  X1:214 X2:508 Y1:374 Y2:410\r\nlf life were only like this.\r\n\r\n207\r\n00:12:14,107 --> 00:12:18,232  X1:155 X2:567 Y1:070 Y2:145\r\nJune 14, 1940.\r\nThe German army occupies Paris.\r\n\r\n208\r\n00:12:18,300 --> 00:12:22,230  X1:169 X2:553 Y1:070 Y2:145\r\nAll over the country,\r\npeople are desperate for food.\r\n\r\n209\r\n00:12:22,301 --> 00:12:25,750  X1:141 X2:581 Y1:334 Y2:409\r\nThose guys in the French Resistance\r\nwere really brave.\r\n\r\n210\r\n00:12:25,822 --> 00:12:28,506  X1:152 X2:570 Y1:334 Y2:409\r\nTo have to listen\r\nto Maurice Chevalier sing so much.\r\n\r\n211\r\n00:12:28,575 --> 00:12:32,286  X1:173 X2:549 Y1:334 Y2:409\r\nl know. Sometimes l ask myself\r\nhow l\'d stand up under torture.\r\n\r\n212\r\n00:12:32,352 --> 00:12:34,262  X1:248 X2:474 Y1:374 Y2:410\r\nYou? You kidding?\r\n\r\n213\r\n00:12:34,337 --> 00:12:38,167  X1:099 X2:622 Y1:334 Y2:409\r\nThe Gestapo would take away your charge\r\ncard, and you\'d tell \'em everything.\r\n\r\n214\r\n00:12:38,242 --> 00:12:40,185  X1:161 X2:561 Y1:374 Y2:410\r\nThat movie makes me feel guilty.\r\n\r\n215\r\n00:12:40,259 --> 00:12:42,299  X1:260 X2:462 Y1:374 Y2:410\r\nlt\'s supposed to.\r\n\r\n216\r\n00:12:43,523 --> 00:12:46,207  X1:304 X2:417 Y1:334 Y2:409\r\n- Alvy, l--\r\n- What?\r\n\r\n217\r\n00:12:46,276 --> 00:12:48,481  X1:238 X2:483 Y1:374 Y2:410\r\nWhat\'s the matter?\r\n\r\n218\r\n00:12:49,477 --> 00:12:52,259  X1:161 X2:560 Y1:334 Y2:409\r\nlt\'s not natural.\r\nWe\'re sleeping in a bed together.\r\n\r\n219\r\n00:12:52,326 --> 00:12:54,181  X1:237 X2:484 Y1:374 Y2:410\r\nlt\'s been a long time!\r\n\r\n220\r\n00:12:54,246 --> 00:12:56,614  X1:257 X2:464 Y1:374 Y2:410\r\nlt\'s just, l mean--\r\n\r\n221\r\n00:12:56,679 --> 00:12:59,233  X1:169 X2:552 Y1:334 Y2:409\r\nl\'ve got to sing tomorrow night,\r\nso l have to rest my voice.\r\n\r\n222\r\n00:12:59,304 --> 00:13:02,436  X1:149 X2:573 Y1:334 Y2:409\r\nlt\'s always some kind of an excuse.\r\nYou used to think l was very sexy.\r\n\r\n223\r\n00:13:02,504 --> 00:13:05,123  X1:161 X2:561 Y1:334 Y2:409\r\nWhen we first started going out,\r\nwe had sex constantly.\r\n\r\n224\r\n00:13:05,193 --> 00:13:07,714  X1:142 X2:579 Y1:334 Y2:409\r\nWe\'re probably listed in\r\nThe Guiness Book of World Records.\r\n\r\n225\r\n00:13:07,786 --> 00:13:11,496  X1:198 X2:523 Y1:334 Y2:409\r\nlt\'ll pass. l\'m going\r\nthrough a phase, that\'s all.\r\n\r\n226\r\n00:13:11,562 --> 00:13:13,952  X1:181 X2:540 Y1:334 Y2:409\r\nYou\'ve been married before.\r\nYou know how things can get.\r\n\r\n227\r\n00:13:14,027 --> 00:13:16,635  X1:245 X2:476 Y1:334 Y2:409\r\nYou were very hot\r\nfor Allison at first.\r\n\r\n228\r\n00:13:20,413 --> 00:13:24,505  X1:120 X2:601 Y1:334 Y2:409\r\nYou know that you\'re on right\r\nafter Chris Brown, in about 20 minutes.\r\n\r\n229\r\n00:13:24,573 --> 00:13:26,483  X1:260 X2:462 Y1:334 Y2:409\r\nExcuse me.\r\nWhen do l go on?\r\n\r\n230\r\n00:13:26,558 --> 00:13:28,468  X1:277 X2:445 Y1:374 Y2:410\r\nWho are you?\r\n\r\n231\r\n00:13:28,543 --> 00:13:30,878  X1:287 X2:434 Y1:374 Y2:410\r\nAlvy Singer.\r\n\r\n232\r\n00:13:30,943 --> 00:13:33,497  X1:248 X2:474 Y1:334 Y2:409\r\n- l\'m the comedian.\r\n- Oh, comedian.\r\n\r\n233\r\n00:13:33,568 --> 00:13:36,286  X1:249 X2:472 Y1:374 Y2:410\r\nOh, you\'re on next.\r\n\r\n234\r\n00:13:36,353 --> 00:13:38,393  X1:201 X2:521 Y1:374 Y2:410\r\nWhat do you mean \"next\"?\r\n\r\n235\r\n00:13:38,465 --> 00:13:40,506  X1:181 X2:540 Y1:374 Y2:410\r\nYou\'re on right after this act.\r\n\r\n236\r\n00:13:40,578 --> 00:13:43,197  X1:156 X2:566 Y1:334 Y2:409\r\n- lt can\'t be, because he\'s a comic.\r\n- Yes.\r\n\r\n237\r\n00:13:43,266 --> 00:13:46,562  X1:117 X2:605 Y1:334 Y2:409\r\n- You\'re putting on two comics in a row?\r\n- Why not?\r\n\r\n238\r\n00:13:46,628 --> 00:13:49,858  X1:152 X2:570 Y1:334 Y2:409\r\nNo, l\'m sorry. l\'m not going to go on\r\nafter another comedian.\r\n\r\n239\r\n00:13:49,925 --> 00:13:54,116  X1:142 X2:579 Y1:334 Y2:409\r\n- lt\'s okay.\r\n- No. They\'re laughing. l\'d rather not.\r\n\r\n240\r\n00:13:54,181 --> 00:13:56,549  X1:207 X2:515 Y1:334 Y2:409\r\nWill you relax, please?\r\nThey\'re going to love you.\r\n\r\n241\r\n00:13:56,614 --> 00:14:01,55  X1:184 X2:537 Y1:334 Y2:409\r\nl know. l prefer not to.\r\nLook, they\'re laughing at him.\r\n\r\n242\r\n00:14:01,62 --> 00:14:01,795  X1:153 X2:568 Y1:334 Y2:409\r\nWhat are you telling me?\r\nThey\'ll laugh at him a few minutes.\r\n\r\n243\r\n00:14:01,863 --> 00:14:04,799  X1:170 X2:551 Y1:334 Y2:409\r\nThen l\'ve got to get laughs, too.\r\nHow much can they laugh?\r\n\r\n244\r\n00:14:04,873 --> 00:14:07,524  X1:227 X2:495 Y1:334 Y2:409\r\n- They\'re laughed out.\r\n- Do you feel all right?\r\n\r\n245\r\n00:14:07,593 --> 00:14:09,634  X1:321 X2:400 Y1:374 Y2:410\r\nJesus.\r\n\r\n246\r\n00:14:10,634 --> 00:14:13,482  X1:234 X2:487 Y1:334 Y2:409\r\n- What\'s your name?\r\n- Allison.\r\n\r\n247\r\n00:14:13,547 --> 00:14:15,914  X1:234 X2:488 Y1:374 Y2:410\r\nYeah? Allison what?\r\n\r\n248\r\n00:14:15,979 --> 00:14:17,987  X1:298 X2:423 Y1:374 Y2:410\r\nPortchnik.\r\n\r\n249\r\n00:14:18,348 --> 00:14:21,927  X1:213 X2:508 Y1:334 Y2:409\r\n- Portchnik? That\'s nice.\r\n- Thank you.\r\n\r\n250\r\n00:14:24,814 --> 00:14:28,612  X1:192 X2:529 Y1:334 Y2:409\r\nSo, you work for Stevenson\r\nall the time?\r\n\r\n251\r\n00:14:28,687 --> 00:14:32,617  X1:214 X2:508 Y1:334 Y2:409\r\n- No. l\'m doing my thesis.\r\n- On what?\r\n\r\n252\r\n00:14:32,689 --> 00:14:35,723  X1:199 X2:523 Y1:334 Y2:409\r\nPolitical commitment\r\nin 20th-century literature.\r\n\r\n253\r\n00:14:35,793 --> 00:14:39,023  X1:186 X2:535 Y1:334 Y2:409\r\nYou\'re like New York Jewish,\r\nleft-wing, intellectual...\r\n\r\n254\r\n00:14:39,090 --> 00:14:42,222  X1:118 X2:603 Y1:334 Y2:409\r\nCentral Park West, Brandeis University,\r\nsocialist summer camps...\r\n\r\n255\r\n00:14:42,291 --> 00:14:45,325  X1:176 X2:545 Y1:334 Y2:409\r\nand the father\r\nwith the Ben Shahn drawings...\r\n\r\n256\r\n00:14:45,395 --> 00:14:48,396  X1:218 X2:504 Y1:374 Y2:410\r\nreally strike-oriented--\r\n\r\n257\r\n00:14:48,468 --> 00:14:50,738  X1:175 X2:547 Y1:334 Y2:409\r\nStop me before l make\r\na complete imbecile of myself.\r\n\r\n258\r\n00:14:50,805 --> 00:14:55,061  X1:112 X2:610 Y1:334 Y2:409\r\nThat was wonderful! l love being reduced\r\nto a cultural stereotype.\r\n\r\n259\r\n00:14:55,127 --> 00:14:58,477  X1:256 X2:465 Y1:334 Y2:409\r\nRight. l\'m a bigot,\r\nbut for the left.\r\n\r\n260\r\n00:15:00,439 --> 00:15:04,335  X1:141 X2:581 Y1:334 Y2:409\r\nl have to go out there.\r\nSay something encouraging, quickly.\r\n\r\n261\r\n00:15:04,409 --> 00:15:07,923  X1:243 X2:479 Y1:334 Y2:409\r\n- l think you\'re cute.\r\n- Yeah? Do you?\r\n\r\n262\r\n00:15:08,506 --> 00:15:10,099  X1:299 X2:422 Y1:374 Y2:410\r\nGo ahead.\r\n\r\n263\r\n00:15:20,509 --> 00:15:23,292  X1:139 X2:583 Y1:334 Y2:409\r\nl don\'t know why they would have me\r\nat this kind of rally...\r\n\r\n264\r\n00:15:23,358 --> 00:15:26,904  X1:209 X2:512 Y1:334 Y2:409\r\n\'cause l\'m not essentially\r\na political comedian.\r\n\r\n265\r\n00:15:26,974 --> 00:15:31,449  X1:146 X2:576 Y1:374 Y2:410\r\nl interestingly had dated a woman...\r\n\r\n266\r\n00:15:31,520 --> 00:15:34,488  X1:157 X2:564 Y1:334 Y2:409\r\nin the Eisenhower administration,\r\nbriefly.\r\n\r\n267\r\n00:15:34,561 --> 00:15:37,212  X1:240 X2:481 Y1:374 Y2:410\r\nlt was ironic to me...\r\n\r\n268\r\n00:15:38,466 --> 00:15:41,117  X1:165 X2:556 Y1:374 Y2:410\r\n\'cause l was trying to do to her...\r\n\r\n269\r\n00:15:41,186 --> 00:15:44,187  X1:126 X2:596 Y1:334 Y2:409\r\nwhat Eisenhower\'s been doing\r\nto the country for the last eight years.\r\n\r\n270\r\n00:15:48,133 --> 00:15:50,173  X1:196 X2:525 Y1:334 Y2:409\r\nl\'m sorry.\r\nl can\'t go through with this.\r\n\r\n271\r\n00:15:50,245 --> 00:15:53,562  X1:209 X2:512 Y1:334 Y2:409\r\nl can\'t get it off my mind!\r\nlt\'s obsessing me!\r\n\r\n272\r\n00:15:53,638 --> 00:15:56,486  X1:231 X2:491 Y1:334 Y2:409\r\nl\'m getting tired of it.\r\nl need your attention.\r\n\r\n273\r\n00:15:56,551 --> 00:16:00,512  X1:141 X2:581 Y1:334 Y2:409\r\nBut it doesn\'t make any sense.\r\nHe drove past the book depository...\r\n\r\n274\r\n00:16:00,583 --> 00:16:03,683  X1:172 X2:550 Y1:334 Y2:409\r\nand the police said conclusively\r\nthat it was an exit wound.\r\n\r\n275\r\n00:16:03,753 --> 00:16:07,944  X1:120 X2:602 Y1:334 Y2:409\r\nSo how is it possible for Oswald\r\nto have fired from two angles at once?\r\n\r\n276\r\n00:16:08,009 --> 00:16:09,985  X1:225 X2:496 Y1:374 Y2:410\r\nlt doesn\'t make sense!\r\n\r\n277\r\n00:16:11,211 --> 00:16:14,528  X1:170 X2:552 Y1:334 Y2:409\r\nl\'ll tell you this.\r\nHe was not marksman enough...\r\n\r\n278\r\n00:16:14,604 --> 00:16:16,993  X1:139 X2:583 Y1:374 Y2:410\r\nto hit a moving target at that range.\r\n\r\n279\r\n00:16:17,068 --> 00:16:21,379  X1:137 X2:584 Y1:374 Y2:410\r\nBut if there was a second assassin--\r\n\r\n280\r\n00:16:21,453 --> 00:16:23,909  X1:203 X2:519 Y1:334 Y2:409\r\n- That\'s it!\r\n- We\'ve been through this.\r\n\r\n281\r\n00:16:23,982 --> 00:16:26,438  X1:204 X2:518 Y1:334 Y2:409\r\nThey recovered the shells\r\nfrom that rifle.\r\n\r\n282\r\n00:16:26,510 --> 00:16:28,453  X1:200 X2:522 Y1:374 Y2:410\r\nWhat are you saying now?\r\n\r\n283\r\n00:16:28,527 --> 00:16:32,805  X1:098 X2:623 Y1:334 Y2:409\r\nThat everybody on the Warren Commission\r\nis in on this conspiracy, right?\r\n\r\n284\r\n00:16:32,880 --> 00:16:34,474  X1:302 X2:419 Y1:374 Y2:410\r\nWhy not?\r\n\r\n285\r\n00:16:34,545 --> 00:16:38,539  X1:158 X2:563 Y1:334 Y2:409\r\n- Earl Warren?\r\n- Honey, l don\'t know Earl Warren.\r\n\r\n286\r\n00:16:38,610 --> 00:16:40,498  X1:256 X2:466 Y1:374 Y2:410\r\nLyndon Johnson?\r\n\r\n287\r\n00:16:40,562 --> 00:16:42,832  X1:180 X2:541 Y1:374 Y2:410\r\nLyndon Johnson is a politician!\r\n\r\n288\r\n00:16:42,899 --> 00:16:47,308  X1:129 X2:592 Y1:334 Y2:409\r\nYou know the ethics those guys have.\r\nlt\'s a notch underneath child molester.\r\n\r\n289\r\n00:16:47,380 --> 00:16:49,715  X1:127 X2:594 Y1:374 Y2:410\r\nThen everybody\'s in on the conspiracy.\r\n\r\n290\r\n00:16:49,781 --> 00:16:54,900  X1:117 X2:605 Y1:334 Y2:409\r\nThe FBl and the ClA and J. Edgar Hoover,\r\nthe oil companies...\r\n\r\n291\r\n00:16:54,966 --> 00:16:58,164  X1:110 X2:611 Y1:334 Y2:409\r\nthe Pentagon, the men\'s-room attendant\r\nat the White House?\r\n\r\n292\r\n00:16:58,232 --> 00:17:01,266  X1:195 X2:526 Y1:334 Y2:409\r\nl would leave out\r\nthe men\'s-room attendant.\r\n\r\n293\r\n00:17:01,336 --> 00:17:06,487  X1:146 X2:575 Y1:334 Y2:409\r\nYou\'re using this conspiracy theory\r\nas an excuse to avoid sex with me.\r\n\r\n294\r\n00:17:06,554 --> 00:17:09,751  X1:289 X2:432 Y1:334 Y2:409\r\nOh, my God!\r\nShe\'s right.\r\n\r\n295\r\n00:17:09,818 --> 00:17:11,924  X1:143 X2:579 Y1:374 Y2:410\r\nWhy did l turn off Allison Portchnik?\r\n\r\n296\r\n00:17:11,995 --> 00:17:15,793  X1:150 X2:571 Y1:334 Y2:409\r\nShe was beautiful. She was willing.\r\nShe was real intelligent.\r\n\r\n297\r\n00:17:15,868 --> 00:17:18,619  X1:169 X2:553 Y1:374 Y2:410\r\nls it the old Groucho Marx joke...\r\n\r\n298\r\n00:17:18,685 --> 00:17:22,483  X1:133 X2:589 Y1:334 Y2:409\r\nthat l don\'t want to belong to any club\r\nthat would have me for a member?\r\n\r\n299\r\n00:17:22,622 --> 00:17:25,972  X1:249 X2:473 Y1:334 Y2:409\r\n- Alvy, don\'t panic!\r\n- lt\'s a mistake...\r\n\r\n300\r\n00:17:26,047 --> 00:17:27,902  X1:168 X2:554 Y1:374 Y2:410\r\nto bring a live thing in the house.\r\n\r\n301\r\n00:17:27,967 --> 00:17:29,877  X1:278 X2:444 Y1:374 Y2:410\r\nDon\'t do that.\r\n\r\n302\r\n00:17:29,952 --> 00:17:33,335  X1:169 X2:552 Y1:334 Y2:409\r\nMaybe we should call the police.\r\nDial 911. lt\'s the lobster squad.\r\n\r\n303\r\n00:17:33,409 --> 00:17:35,831  X1:220 X2:502 Y1:334 Y2:409\r\nThey\'re only baby ones.\r\nFor God sakes.\r\n\r\n304\r\n00:17:35,905 --> 00:17:38,906  X1:143 X2:579 Y1:334 Y2:409\r\n- lf they\'re babies, you pick them up.\r\n- All right! All right!\r\n\r\n305\r\n00:17:38,979 --> 00:17:41,762  X1:240 X2:482 Y1:334 Y2:409\r\n- Here you go!\r\n- Don\'t give it to me.\r\n\r\n306\r\n00:17:41,827 --> 00:17:44,992  X1:213 X2:508 Y1:334 Y2:409\r\nLook! One crawled\r\nbehind the refrigerator.\r\n\r\n307\r\n00:17:45,060 --> 00:17:48,738  X1:171 X2:551 Y1:334 Y2:409\r\nlt\'ll turn up in our bed at night.\r\nGet out of here with that thing!\r\n\r\n308\r\n00:17:50,342 --> 00:17:52,764  X1:239 X2:482 Y1:334 Y2:409\r\nTalk to him.\r\nYou speak shellfish.\r\n\r\n309\r\n00:17:52,838 --> 00:17:55,556  X1:236 X2:485 Y1:374 Y2:410\r\nHey, put it in the pot.\r\n\r\n310\r\n00:17:55,623 --> 00:17:59,006  X1:190 X2:531 Y1:334 Y2:409\r\nl can\'t. l can\'t put a live thing\r\nin hot water.\r\n\r\n311\r\n00:17:59,080 --> 00:18:01,252  X1:131 X2:590 Y1:334 Y2:409\r\nWhat d\'you think we were going to do,\r\ntake him to the movies?\r\n\r\n312\r\n00:18:01,320 --> 00:18:03,328  X1:238 X2:484 Y1:374 Y2:410\r\nGood. Oh, thank you.\r\n\r\n313\r\n00:18:03,401 --> 00:18:06,271  X1:217 X2:504 Y1:334 Y2:409\r\nOkay, it\'s in.\r\nlt\'s definitely in the pot.\r\n\r\n314\r\n00:18:06,346 --> 00:18:08,997  X1:197 X2:525 Y1:334 Y2:409\r\nAnnie, there\'s a big lobster\r\nbehind the refrigerator.\r\n\r\n315\r\n00:18:09,066 --> 00:18:11,882  X1:249 X2:473 Y1:334 Y2:409\r\nl can\'t get it out.\r\nThis thing\'s heavy.\r\n\r\n316\r\n00:18:11,947 --> 00:18:15,690  X1:142 X2:579 Y1:334 Y2:409\r\nMaybe if l put a dish of butter sauce\r\nhere, it\'ll run out the other side.\r\n\r\n317\r\n00:18:15,756 --> 00:18:17,612  X1:193 X2:528 Y1:374 Y2:410\r\nl\'m going to get my camera.\r\n\r\n318\r\n00:18:17,677 --> 00:18:20,166  X1:237 X2:484 Y1:374 Y2:410\r\nl think if l could pry--\r\n\r\n319\r\n00:18:20,237 --> 00:18:23,587  X1:131 X2:590 Y1:334 Y2:409\r\nWe should have gotten steaks, \'cause\r\nthey don\'t have legs and run around.\r\n\r\n320\r\n00:18:26,992 --> 00:18:30,124  X1:138 X2:583 Y1:334 Y2:409\r\n- Pick this lobster up. Hold it, please.\r\n- You\'re going to take pictures now?\r\n\r\n321\r\n00:18:30,192 --> 00:18:33,739  X1:222 X2:500 Y1:334 Y2:409\r\nAlvy, it\'ll be wonderful.\r\nOh, lovely!\r\n\r\n322\r\n00:18:33,810 --> 00:18:37,356  X1:174 X2:548 Y1:334 Y2:409\r\n- lt\'s disgusting.\r\n- One more, please? One more.\r\n\r\n323\r\n00:18:37,426 --> 00:18:38,801  X1:325 X2:397 Y1:374 Y2:410\r\nGood!\r\n\r\n324\r\n00:18:38,867 --> 00:18:42,184  X1:182 X2:539 Y1:334 Y2:409\r\nHere\'s what l want to know.\r\nAm l your first, big romance?\r\n\r\n325\r\n00:18:44,692 --> 00:18:48,522  X1:139 X2:583 Y1:334 Y2:409\r\n- Who was?\r\n- There was Dennis from high school.\r\n\r\n326\r\n00:18:48,597 --> 00:18:53,203  X1:126 X2:596 Y1:334 Y2:409\r\nLocal kid who\'d meet you in front\r\nof the movie house on Saturday night?\r\n\r\n327\r\n00:18:53,271 --> 00:18:58,007  X1:128 X2:593 Y1:334 Y2:409\r\n- You should\'ve seen how l looked then.\r\n- l can imagine. An astronaut\'s wife.\r\n\r\n328\r\n00:18:58,072 --> 00:19:00,920  X1:152 X2:569 Y1:334 Y2:409\r\n- Then there was Jerry, the actor.\r\n- Look at you.\r\n\r\n329\r\n00:19:00,985 --> 00:19:02,840  X1:235 X2:486 Y1:374 Y2:410\r\nYou\'re such a clown.\r\n\r\n330\r\n00:19:02,905 --> 00:19:06,289  X1:127 X2:594 Y1:334 Y2:409\r\n- l look pretty.\r\n- You always do. But that guy with you!\r\n\r\n331\r\n00:19:06,362 --> 00:19:10,706  X1:183 X2:539 Y1:334 Y2:409\r\nActing is like an exploration\r\nof the soul. lt\'s very religious.\r\n\r\n332\r\n00:19:10,779 --> 00:19:14,162  X1:207 X2:515 Y1:334 Y2:409\r\nLike a kind of\r\nliberating consciousness.\r\n\r\n333\r\n00:19:14,236 --> 00:19:16,408  X1:230 X2:492 Y1:374 Y2:410\r\nlt\'s like a visual poem.\r\n\r\n334\r\n00:19:16,476 --> 00:19:18,452  X1:188 X2:533 Y1:374 Y2:410\r\nls he kidding with that crap?\r\n\r\n335\r\n00:19:18,525 --> 00:19:20,763  X1:304 X2:417 Y1:374 Y2:410\r\nOh, right!\r\n\r\n336\r\n00:19:20,830 --> 00:19:24,147  X1:151 X2:570 Y1:334 Y2:409\r\nRight. l think l know exactly what\r\nyou mean when you say \"religious.\"\r\n\r\n337\r\n00:19:24,223 --> 00:19:26,842  X1:207 X2:515 Y1:334 Y2:409\r\n- You do?\r\n- Come on. l was younger.\r\n\r\n338\r\n00:19:26,912 --> 00:19:28,767  X1:242 X2:480 Y1:374 Y2:410\r\nThat was last year!\r\n\r\n339\r\n00:19:28,832 --> 00:19:31,680  X1:189 X2:532 Y1:334 Y2:409\r\nlt\'s like when l think of dying.\r\nYou know how l\'d like to die?\r\n\r\n340\r\n00:19:31,745 --> 00:19:33,119  X1:325 X2:397 Y1:374 Y2:410\r\nHow?\r\n\r\n341\r\n00:19:33,185 --> 00:19:36,153  X1:216 X2:506 Y1:334 Y2:409\r\nl\'d like to get torn apart\r\nby wild animals.\r\n\r\n342\r\n00:19:36,226 --> 00:19:39,839  X1:160 X2:562 Y1:334 Y2:409\r\n- Heavy! Eaten by some squirrels.\r\n- He was a terrific actor.\r\n\r\n343\r\n00:19:39,907 --> 00:19:43,105  X1:226 X2:496 Y1:334 Y2:409\r\nHe\'s neat looking\r\nand he was emotional.\r\n\r\n344\r\n00:19:43,172 --> 00:19:45,311  X1:244 X2:478 Y1:334 Y2:409\r\nl don\'t think you like\r\nemotion too much.\r\n\r\n345\r\n00:19:45,380 --> 00:19:48,098  X1:173 X2:549 Y1:374 Y2:410\r\nTouch my heart with your foot.\r\n\r\n346\r\n00:19:48,165 --> 00:19:49,921  X1:268 X2:454 Y1:374 Y2:410\r\nl may throw up.\r\n\r\n347\r\n00:19:49,990 --> 00:19:52,412  X1:259 X2:462 Y1:334 Y2:409\r\n- He was creepy.\r\n- Yeah!\r\n\r\n348\r\n00:19:52,487 --> 00:19:57,605  X1:155 X2:567 Y1:334 Y2:409\r\n- You\'re pretty lucky l came along.\r\n- Oh, really? La-de-dah!\r\n\r\n349\r\n00:19:57,672 --> 00:20:01,349  X1:193 X2:529 Y1:334 Y2:409\r\nlf anyone ever told me\r\nthat l\'d be taking out a girl...\r\n\r\n350\r\n00:20:01,417 --> 00:20:03,392  X1:226 X2:496 Y1:334 Y2:409\r\nwho used expressions\r\nlike \"la-de-dah\"--\r\n\r\n351\r\n00:20:03,466 --> 00:20:06,565  X1:199 X2:522 Y1:334 Y2:409\r\nThat\'s right. You really like\r\nthose New York girls.\r\n\r\n352\r\n00:20:06,634 --> 00:20:09,897  X1:135 X2:586 Y1:334 Y2:409\r\n- Not just. Not only.\r\n- l\'d say so. You married two of them.\r\n\r\n353\r\n00:20:09,963 --> 00:20:10,507  X1:221 X2:500 Y1:374 Y2:410\r\nThere\'s Henry Drucker.\r\n\r\n354\r\n00:20:10,507 --> 00:20:12,450  X1:221 X2:500 Y1:374 Y2:410\r\nThere\'s Henry Drucker.\r\n\r\n355\r\n00:20:12,524 --> 00:20:14,564  X1:214 X2:508 Y1:334 Y2:409\r\nHe has a Chair in History\r\nat Princeton.\r\n\r\n356\r\n00:20:14,637 --> 00:20:18,664  X1:128 X2:594 Y1:334 Y2:409\r\nThe short man is Hershel Kaminsky.\r\nHe has a Chair in Philosophy at Cornell.\r\n\r\n357\r\n00:20:18,734 --> 00:20:20,589  X1:199 X2:522 Y1:334 Y2:409\r\nTwo more chairs,\r\nthey got a dining room set.\r\n\r\n358\r\n00:20:20,655 --> 00:20:23,951  X1:151 X2:571 Y1:334 Y2:409\r\n- Why are you so hostile?\r\n- l want to watch the Knicks on TV.\r\n\r\n359\r\n00:20:24,015 --> 00:20:26,187  X1:201 X2:521 Y1:374 Y2:410\r\nls that Paul Goodman? No.\r\n\r\n360\r\n00:20:26,256 --> 00:20:29,224  X1:163 X2:559 Y1:334 Y2:409\r\nBe nice to the host,\r\nbecause he\'s publishing my book.\r\n\r\n361\r\n00:20:29,297 --> 00:20:32,297  X1:260 X2:462 Y1:334 Y2:409\r\nHi, Doug.\r\nDouglas Wyeth...\r\n\r\n362\r\n00:20:32,370 --> 00:20:34,923  X1:187 X2:535 Y1:334 Y2:409\r\nThe Foul Rag-and-Bone Shop\r\nof the Heart.\r\n\r\n363\r\n00:20:34,994 --> 00:20:36,620  X1:222 X2:500 Y1:334 Y2:409\r\nl\'m so tired\r\nof spending evenings...\r\n\r\n364\r\n00:20:36,690 --> 00:20:38,633  X1:135 X2:587 Y1:334 Y2:409\r\nmaking fake insights\r\nwith people who work for Dysentery.\r\n\r\n365\r\n00:20:38,707 --> 00:20:40,081  X1:276 X2:445 Y1:374 Y2:410\r\nCommentary.\r\n\r\n366\r\n00:20:40,148 --> 00:20:43,978  X1:144 X2:577 Y1:334 Y2:409\r\nl heard Commentary and Dissent\r\nhad merged and formed Dysentery.\r\n\r\n367\r\n00:20:44,053 --> 00:20:46,988  X1:206 X2:516 Y1:334 Y2:409\r\nNo jokes.\r\nThese are friends. Okay?\r\n\r\n368\r\n00:20:47,061 --> 00:20:50,378  X1:199 X2:522 Y1:334 Y2:409\r\nCleveland Cavaliers losing\r\nto the New York Knicks.\r\n\r\n369\r\n00:20:50,454 --> 00:20:54,481  X1:207 X2:514 Y1:334 Y2:409\r\nHere you are.\r\nThere\'s people out there.\r\n\r\n370\r\n00:20:54,551 --> 00:20:57,235  X1:163 X2:558 Y1:334 Y2:409\r\nTwo minutes ago,\r\nthe Knicks are ahead 14 points...\r\n\r\n371\r\n00:20:57,304 --> 00:21:01,69  X1:151 X2:570 Y1:374 Y2:410\r\nand now they\'re ahead two points.\r\n\r\n372\r\n00:21:00,025 --> 00:21:03,474  X1:182 X2:539 Y1:374 Y2:410\r\nAlvy, what is so fascinating...\r\n\r\n373\r\n00:21:03,546 --> 00:21:08,217  X1:133 X2:588 Y1:334 Y2:409\r\nabout a group of pituitary cases\r\ntrying to stuff a ball through a hoop?\r\n\r\n374\r\n00:21:08,283 --> 00:21:10,323  X1:241 X2:481 Y1:334 Y2:409\r\nWhat is fascinating\r\nis that it\'s physical.\r\n\r\n375\r\n00:21:10,395 --> 00:21:12,250  X1:144 X2:578 Y1:374 Y2:410\r\nThat\'s one thing about intellectuals:\r\n\r\n376\r\n00:21:12,316 --> 00:21:15,797  X1:151 X2:571 Y1:334 Y2:409\r\nThey\'ve proved you can be brilliant\r\nand have no idea what\'s going on.\r\n\r\n377\r\n00:21:15,869 --> 00:21:20,507  X1:139 X2:583 Y1:334 Y2:409\r\nBut, on the other hand,\r\nthe body doesn\'t lie as we now know.\r\n\r\n378\r\n00:21:21,791 --> 00:21:23,285  X1:263 X2:459 Y1:374 Y2:410\r\nStop acting out.\r\n\r\n379\r\n00:21:23,359 --> 00:21:26,360  X1:175 X2:547 Y1:334 Y2:409\r\nlt\'ll be great, because all those\r\nPh.D.s are in there discussing...\r\n\r\n380\r\n00:21:26,432 --> 00:21:30,142  X1:146 X2:575 Y1:334 Y2:409\r\nmodes of alienation,\r\nand we\'ll be in here quietly humping.\r\n\r\n381\r\n00:21:30,208 --> 00:21:34,585  X1:224 X2:498 Y1:334 Y2:409\r\nDon\'t. You\'re using sex\r\nto express hostility.\r\n\r\n382\r\n00:21:34,658 --> 00:21:39,296  X1:126 X2:596 Y1:334 Y2:409\r\n\"Why do you always reduce my animal\r\nurges to psychoanalytic categories,\"...\r\n\r\n383\r\n00:21:39,363 --> 00:21:42,266  X1:145 X2:576 Y1:374 Y2:410\r\nhe said as he removed her brassier.\r\n\r\n384\r\n00:21:44,004 --> 00:21:48,097  X1:164 X2:557 Y1:334 Y2:409\r\nThere are people out there\r\nfrom the New Yorker magazine!\r\n\r\n385\r\n00:21:48,166 --> 00:21:51,068  X1:217 X2:504 Y1:334 Y2:409\r\nMy God!\r\nWhat would they think?\r\n\r\n386\r\n00:21:57,576 --> 00:22:00,392  X1:255 X2:467 Y1:334 Y2:409\r\n- Damn siren!\r\n- Don\'t get upset.\r\n\r\n387\r\n00:22:00,457 --> 00:22:02,246  X1:222 X2:499 Y1:374 Y2:410\r\nDamn it! l was so close!\r\n\r\n388\r\n00:22:02,313 --> 00:22:04,900  X1:223 X2:498 Y1:334 Y2:409\r\nLast night it was a guy\r\nhonking his car horn.\r\n\r\n389\r\n00:22:04,971 --> 00:22:07,841  X1:205 X2:516 Y1:374 Y2:410\r\nThe city can\'t close down.\r\n\r\n390\r\n00:22:07,915 --> 00:22:11,298  X1:147 X2:574 Y1:334 Y2:409\r\nYou want them to close the airport\r\nso we can have sex?\r\n\r\n391\r\n00:22:11,372 --> 00:22:13,926  X1:267 X2:454 Y1:334 Y2:409\r\nl\'m too tense.\r\nl need a Valium.\r\n\r\n392\r\n00:22:13,997 --> 00:22:16,867  X1:151 X2:570 Y1:334 Y2:409\r\nMy analyst says l should live\r\nin the country and not in New York.\r\n\r\n393\r\n00:22:16,942 --> 00:22:20,325  X1:112 X2:609 Y1:334 Y2:409\r\nWe can\'t have this discussion all\r\nthe time. The country makes me nervous.\r\n\r\n394\r\n00:22:20,398 --> 00:22:24,109  X1:133 X2:589 Y1:334 Y2:409\r\nYou\'ve got crickets. lt\'s quiet.\r\nThere\'s no place to walk after dinner.\r\n\r\n395\r\n00:22:24,176 --> 00:22:27,079  X1:157 X2:565 Y1:334 Y2:409\r\nThere\'s the screens\r\nwith the dead moths behind them.\r\n\r\n396\r\n00:22:27,153 --> 00:22:30,023  X1:140 X2:582 Y1:334 Y2:409\r\nYou got the Manson family, possibly.\r\nYou got Dick and Perry.\r\n\r\n397\r\n00:22:30,097 --> 00:22:34,887  X1:135 X2:587 Y1:334 Y2:409\r\nOkay! My analyst just thinks l\'m too\r\ntense! Where\'s the goddamn Valium?\r\n\r\n398\r\n00:22:34,962 --> 00:22:37,265  X1:202 X2:520 Y1:334 Y2:409\r\nOkay, it\'s quiet now.\r\nWe can get started again.\r\n\r\n399\r\n00:22:37,331 --> 00:22:39,982  X1:191 X2:531 Y1:374 Y2:410\r\nl can\'t! My head is throbbing.\r\n\r\n400\r\n00:22:40,051 --> 00:22:42,092  X1:223 X2:498 Y1:334 Y2:409\r\n- You got a headache?\r\n- l have a headache!\r\n\r\n401\r\n00:22:42,165 --> 00:22:44,915  X1:217 X2:505 Y1:334 Y2:409\r\n- Bad?\r\n- Like Oswald in Ghosts.\r\n\r\n402\r\n00:22:44,981 --> 00:22:47,283  X1:219 X2:502 Y1:334 Y2:409\r\n- Jesus.\r\n- Where are you going?\r\n\r\n403\r\n00:22:47,350 --> 00:22:50,612  X1:145 X2:577 Y1:334 Y2:409\r\nl\'m going to take another in a series\r\nof cold showers.\r\n\r\n404\r\n00:22:50,710 --> 00:22:52,947  X1:153 X2:569 Y1:334 Y2:409\r\nMax, my serve is going to send you\r\nto the showers early.\r\n\r\n405\r\n00:22:53,015 --> 00:22:54,925  X1:200 X2:522 Y1:334 Y2:409\r\nRight. To get back to what\r\nwe were discussing.\r\n\r\n406\r\n00:22:55,000 --> 00:22:58,296  X1:121 X2:600 Y1:334 Y2:409\r\nThe failure of the country to get behind\r\nNew York City is anti-Semitism.\r\n\r\n407\r\n00:22:58,360 --> 00:23:00,183  X1:196 X2:525 Y1:374 Y2:410\r\nMax, the city is terribly run.\r\n\r\n408\r\n00:23:00,249 --> 00:23:03,479  X1:179 X2:543 Y1:334 Y2:409\r\nl\'m not discussing politics\r\nor economics. This is foreskin.\r\n\r\n409\r\n00:23:03,547 --> 00:23:07,476  X1:104 X2:617 Y1:334 Y2:409\r\nThat\'s a convenient out. When some group\r\ndisagrees with you, it\'s anti-Semitism.\r\n\r\n410\r\n00:23:07,547 --> 00:23:09,969  X1:218 X2:503 Y1:334 Y2:409\r\nThe rest of the country\r\nlooks upon New York...\r\n\r\n411\r\n00:23:10,044 --> 00:23:13,787  X1:117 X2:605 Y1:334 Y2:409\r\nlike we\'re left-wing, communist, Jewish,\r\nhomosexual pornographers.\r\n\r\n412\r\n00:23:13,853 --> 00:23:16,342  X1:159 X2:562 Y1:334 Y2:409\r\nl think of us that way sometimes,\r\nand l live here.\r\n\r\n413\r\n00:23:16,414 --> 00:23:20,757  X1:131 X2:591 Y1:334 Y2:409\r\nMax, if we lived in California, we could\r\nplay outdoors every day in the sun.\r\n\r\n414\r\n00:23:20,831 --> 00:23:23,865  X1:157 X2:565 Y1:334 Y2:409\r\nSun is bad for you. Everything\r\nour parents said was good is bad.\r\n\r\n415\r\n00:23:23,935 --> 00:23:26,424  X1:193 X2:528 Y1:374 Y2:410\r\nSun, milk, red meat, college.\r\n\r\n416\r\n00:23:28,097 --> 00:23:29,985  X1:232 X2:489 Y1:374 Y2:410\r\nEgad, here he comes.\r\n\r\n417\r\n00:23:33,538 --> 00:23:35,775  X1:263 X2:459 Y1:334 Y2:409\r\nYou know Alvy?\r\nThis is Janet.\r\n\r\n418\r\n00:23:35,842 --> 00:23:39,291  X1:250 X2:472 Y1:334 Y2:409\r\n- This is Annie Hall.\r\n- This is Alvy.\r\n\r\n419\r\n00:23:39,364 --> 00:23:42,812  X1:172 X2:549 Y1:334 Y2:409\r\n- Who\'s playing with who here?\r\n- You and me against them?\r\n\r\n420\r\n00:23:42,885 --> 00:23:44,892  X1:227 X2:494 Y1:334 Y2:409\r\n- That\'s not fair.\r\n- l can\'t play too good.\r\n\r\n421\r\n00:23:44,965 --> 00:23:46,373  X1:232 X2:489 Y1:374 Y2:410\r\nl\'ve had four lessons.\r\n\r\n422\r\n00:24:08,395 --> 00:24:10,338  X1:325 X2:396 Y1:374 Y2:410\r\nWell...\r\n\r\n423\r\n00:24:10,412 --> 00:24:11,841  X1:335 X2:387 Y1:374 Y2:410\r\nbye.\r\n\r\n424\r\n00:24:15,085 --> 00:24:18,348  X1:229 X2:493 Y1:334 Y2:409\r\n- You play very well.\r\n- Oh, yeah? So do you!\r\n\r\n425\r\n00:24:18,414 --> 00:24:21,349  X1:174 X2:547 Y1:374 Y2:410\r\nGod, what a dumb thing to say.\r\n\r\n426\r\n00:24:21,423 --> 00:24:26,094  X1:123 X2:599 Y1:334 Y2:409\r\nYou say \"You play well,\" and right away\r\nl have to say, \"You play well.\"\r\n\r\n427\r\n00:24:26,160 --> 00:24:28,234  X1:269 X2:452 Y1:374 Y2:410\r\nOh, God, Annie.\r\n\r\n428\r\n00:24:28,304 --> 00:24:31,305  X1:279 X2:443 Y1:374 Y2:410\r\nWell. Oh, well!\r\n\r\n429\r\n00:24:31,378 --> 00:24:33,713  X1:293 X2:428 Y1:374 Y2:410\r\nLa-de-dah.\r\n\r\n430\r\n00:24:34,643 --> 00:24:36,531  X1:261 X2:460 Y1:374 Y2:410\r\nYou want a lift?\r\n\r\n431\r\n00:24:36,595 --> 00:24:40,938  X1:270 X2:452 Y1:334 Y2:409\r\nOh, why?\r\nYou got a car?\r\n\r\n432\r\n00:24:41,012 --> 00:24:43,697  X1:184 X2:537 Y1:374 Y2:410\r\nNo, l was going to take a cab.\r\n\r\n433\r\n00:24:43,766 --> 00:24:46,155  X1:245 X2:477 Y1:374 Y2:410\r\nOh, no. l have